const initialState={
        search:"",
        status:'All',
        prioriry:[]
}
const filterReducer =(state=initialState, action)=>{
    console.log(state,action)
    switch(action.type){   
        case 'filter/search':
            return{
                    ...state,
                    search:action.payload
                }
        case 'filter/searchStatus':
            return{
                ...state,
                status:action.p
            }
            
        default:return state    

    }
}
export default filterReducer
