import { createSelector } from "reselect";
export const useSelectorTodolist = (state) => state.todoList;

export const searchFilterChange = (state) => state.filters.search;

export const searhStatusChange =(state)=>status.filters.status;

export const todoRemainingSelector = createSelector(
  useSelectorTodolist,
  searhStatusChange,
  searchFilterChange,
  (todolist,status,searhtext) => {
      return todolist.filter((todo)=>{
          if(status)
         return todo.name.includes(searhtext)
      })
  }
);
